import React, { Fragment, useState }from 'react'
import Calculators from './Calculators'

const INITIAL_STATE = {
    height:'',
    weight:''
}

const Bmi = () => {
    const [formData, setFormData] = useState(INITIAL_STATE);
    const { height, weight } = formData;

    const onChange = e => {
        setFormData({ ...formData, [e.target.name]: e.target.value })
    }
    const onClick = (e) => {
        e.preventDefault();
        showMessage();
        clearMessage();
        setFormData('');
    }
    const showMessage = () => {
        const result = Math.ceil(Math.round(weight)/Math.pow(height, 2) * 10000);
        document.getElementById('result').style.display = 'block'
        let p = document.getElementById('bmi').innerHTML = "Your BMI: " + result;
    }
    const clearMessage = () => {
        setTimeout(() => {
            document.getElementById('result').style.display = 'none';
        }, 4000)
    }
    return (
        <Fragment>
            <Calculators />
            <p className='text-primary large my-1'> BMI Calculator </p>
            <div style={{ display: 'none'}} id='result'>
                <p className='text-primary my-1 lead' id='bmi'></p>
            </div>
            <form className='form'>
                <div className="form-group">
                    <p className='text-primary'><strong>Weight: </strong></p>
                        <input type="text" requirde placeholder="weight" name="weight" value={weight} onChange={e => onChange(e)}/>
                </div>
                <div className="form-group">
                    <p className='text-primary'><strong>Height: </strong></p>
                        <input type="text" requirde placeholder="height" name="height" value={height} onChange={e => onChange(e)}/>
                </div>
                <button onClick={onClick}type="submit" className="btn btn-primary my-1">Calculate</button>
            </form>
        </Fragment>
    )
}

export default Bmi
