import React, { Fragment, useEffect }from 'react'
import PropTypes from 'prop-types'
import {Link} from 'react-router-dom'
import { connect } from 'react-redux'
import { getTrainings, deleteTraining } from '../../actions/training'
import TrainingItem from './TrainingItem'
import TrainingForm from './TrainingForm'

const Trainings = ({ getTrainings, deleteTraining, training: { trainings, _id } }) => {
    useEffect(() => {
        getTrainings()
    }, [getTrainings])
    return (
        <Fragment>
            <h1 className="large text-primary">Trainings</h1>
            <p className="lead">
                <i className="fas fa-user" /> Welcome!
            </p>
            <TrainingForm />
            <div className='posts'>
                <div>
                    {trainings.map((training) => (
                        <TrainingItem key={training._id} training={training} />
                    ))}


                    <button className='btn btn-danger' type ='button' onClick={e => deleteTraining(_id)}>
                        <i className='fas fa-times'></i>
                    </button>

                </div>
            </div>
        </Fragment>
    )
}

Trainings.propTypes = {
    getTrainings: PropTypes.func.isRequired,
    training: PropTypes.object.isRequired,
    deleteTraining: PropTypes.func.isRequired,
}

const mapStateToProps = state => ({
    training: state.training
})

export default connect(mapStateToProps, { getTrainings, deleteTraining })(Trainings)
