import React, { Fragment, useEffect } from 'react'
import PropTypes from 'prop-types'
import { connect } from 'react-redux';
import UserTop from './UserTop';
import UserAbout from './UserAbout';
import { getCurrentUser } from '../../actions/user';
import UserGoalCard from './UserGoalCard';
import { Link } from 'react-router-dom';

const User = ({ auth, getCurrentUser, user }) => {
    useEffect(() => {
        getCurrentUser()
    }, [getCurrentUser])
    return (
        <div>
            {
                auth.isAuthenticated && 
                auth.loading === false &&
                auth.user._id === user._id && (
                    <Link to='/edit-profile' className='btn btn-dark'>Edit Account</Link>
            )}

            <div className="profile-grid my-1">
                <UserTop user={user} />
                <UserAbout user={user}/>
                <div className="profile-exp bg-white p-2">
                    <h2 className='text-primary'>Goal Card</h2>
                    {user.goalCard ? (
                        <Fragment>
                            <UserGoalCard user={user} />
                            {/* {user.goalCard.map(goalCard => (
                                <UserGoalCard key={goalCard._id} user={user}/>
                            ))} */}

                        </Fragment>
                    ) : (
                        <h4> No goals </h4>
                    )
                }
                </div>
            </div>
        </div>
    )
}

User.propTypes = {
    getCurrentUser: PropTypes.func.isRequired,
    user: PropTypes.object.isRequired,
    auth: PropTypes.object.isRequired,
}

const mapStateToProps = state => ({
    user: state.user,
    auth: state.auth
})
export default connect(mapStateToProps, { getCurrentUser })(User)
