import {
    SET_ALERT,
    REMOVE_ALERT
} from '../actions/types';

const INITIAL_STATE = [];

export default function alertReducer(state = INITIAL_STATE, action) {
    const { type, payload } = action;
    switch(type) {
        case SET_ALERT:
            return [...state, payload];
        case REMOVE_ALERT:
            return state.filter(alert => alert.is !== payload);
        default:
            return state;
    }
}
